#include <stdio.h>
#include<stdlib.h>

typedef struct {
	int matricula, idade;
	char nome [15];
	float CR;
}Taluno;

 int main ()
 {
 	Taluno aluno [2], *melhor, *novo;
 	int i;
 	melhor=novo=aluno;
 	i=0;
 	for (i==0; i<2;i++)
 	{
 		printf ("Dados do aluno %d: \n", i+1);
 		printf ("Digite a matricula do aluno: \n");
		scanf ("%d", &aluno[i].matricula);
	    printf ("Digite a idade do aluno: \n");
		scanf ("%d", &aluno[i].idade);
		fflush(stdin);
		printf ("Digite o nome do aluno: \n");
		gets(aluno[i].nome);
		printf("Digite o CR do aluno: \n");
		scanf ("%f", &aluno[i].CR);
	 	if (melhor->CR<aluno[i].CR) melhor = &aluno[i];
	 	if (novo->idade>aluno[i].idade) novo= &aluno[i];
 	}
 	printf("O aluno melhor eh %s \n ", melhor->nome);
 	printf("A matricula do aluno mais jovem eh: %d \n" , novo->matricula);
 	system ("PAUSE");
 	return (0);
 }

