#include <iostream>
using namespace std;
int a=1,b=1;
void processo(int x, int y);
int main(){
  int x,y;
  x=a;y=b;
processo(x,y);
}
void processo(int x, int y){
 int max;
 while (x>0&&y>0){
    cin>>x>>y;
    if (x>y){
	   max=b;}
    else {
	   max=a;
    }
 cout<<"max: " << max<<"\n";
 }
}

#include <iostream>
using namespace std;
int a=1,b=1;
void processo(int x, int y);
int main(){
  int x,y;
  x=a;y=b;
processo(x,y);
}

void processo(int x, int y)
{
 int max;
 while (x>0&&y>0) {
    cin>>x>>y;
    if (x>y) {
	   max=b;
       }
       else {
	   max=a;
    }
   cout<<"max: " << max<<"\n";  
  }
}
}

